mapper.select:
SELECT id, caseid, picturedesc, pictureurl, valid, createtime, createperson, updtime, updperson FROM case_pic_mapping
and id=#{id}
limit 1


SELECT
	id,
	caseid,
	picturedesc,
	pictureurl,
	valid,
	createtime,
	createperson,
	updtime,
	updperson
FROM
	case_pic_mapping
			
	
		and id=#{id}
	
				        
		and caseid=#{caseid}
	
				        
		and picturedesc=#{picturedesc}
	
				        
		and pictureurl=#{pictureurl}
	
				        
		and valid=#{valid}
	
				        
		and createtime=#{createtime}
	
				        
		and createperson=#{createperson}
	
				        
		and updtime=#{updtime}
	
				        
		and updperson=#{updperson}

		
