mapper.select:
SELECT id, caseid, logourl, videodesc, videourl, valid, createtime, createperson, updtime, updperson FROM case_video_mapping
and id=#{id}
limit 1


SELECT
	id,
	caseid,
	logourl,
	videodesc,
	videourl,
	valid,
	createtime,
	createperson,
	updtime,
	updperson
FROM
	case_video_mapping
			
	
		and id=#{id}
	
				        
		and caseid=#{caseid}
	
				        
		and logourl=#{logourl}
	
				        
		and videodesc=#{videodesc}
	
				        
		and videourl=#{videourl}
	
				        
		and valid=#{valid}
	
				        
		and createtime=#{createtime}
	
				        
		and createperson=#{createperson}
	
				        
		and updtime=#{updtime}
	
				        
		and updperson=#{updperson}

		
SELECT
	id,
	caseid,
	logourl,
	videodesc,
	videourl,
	valid,
	createtime,
	createperson,
	updtime,
	updperson
