mapper.select:
SELECT id, area_id, name, country, valid, create_time, create_person, upd_time, upd_person, island_desc, area_name FROM island
and id=#{id}
limit 1


SELECT
	id,
	area_id,
	name,
	country,
	valid,
	create_time,
	create_person,
	upd_time,
	upd_person,
	island_desc,
	area_name
FROM
	island
			
	
		and id=#{id}
	
				        
		and area_id=#{areaId}
	
				        
		and name=#{name}
	
				        
		and country=#{country}
	
				        
		and valid=#{valid}
	
				        
		and create_time=#{createTime}
	
				        
		and create_person=#{createPerson}
	
				        
		and upd_time=#{updTime}
	
				        
		and upd_person=#{updPerson}
	
				        
		and island_desc=#{islandDesc}
	
				        
		and area_name=#{areaName}

		
