	id,
	menu_name,
	menu_url,
	menu_parent,
	create_time,
	show_index,
	valid
FROM
	menu
			
	
		and id=#{id}
	
				        
		and menu_name=#{menuName}
	
				        
		and menu_url=#{menuUrl}
	
				        
		and menu_parent=#{menuParent}
	
				        
		and create_time=#{createTime}
	
				        
		and valid=#{valid}

		
