mapper.select:
SELECT id, package_id, package_type, content, valid, create_time, create_person, upd_time, upd_person FROM package_detail_info
and id=#{id}
limit 1


SELECT
	id,
	package_id,
	package_type,
	content,
	valid,
	create_time,
	create_person,
	upd_time,
	upd_person
FROM
	package_detail_info
			
	
		and id=#{id}
	
				        
		and package_id=#{packageId}
	
				        
		and package_type=#{packageType}
	
				        
		and content=#{content}
	
				        
		and valid=#{valid}
	
				        
		and create_time=#{createTime}
	
				        
		and create_person=#{createPerson}
	
				        
		and upd_time=#{updTime}
	
				        
		and upd_person=#{updPerson}

		
