mapper.select:
SELECT id, role_id, menu_ids, create_time, create_person, upd_time, upd_person, valid FROM role_right
and id=#{id}
limit 1


SELECT
	id,
	role_id,
	menu_ids,
	create_time,
	create_person,
	upd_time,
	upd_person,
	valid
FROM
	role_right
			
	
		and id=#{id}
	
				        
		and role_id=#{roleId}
	
				        
		and menu_ids=#{menuIds}
	
				        
		and create_time=#{createTime}
	
				        
		and create_person=#{createPerson}
	
				        
		and upd_time=#{updTime}
	
				        
		and upd_person=#{updPerson}
	
				        
		and valid=#{valid}

		
